import React from 'react';
import Button from 'react-bootstrap/Button';
import '../../sass/Todo/Task.scss';


const Task = (props) => {

    const clickButton = () => {
        if (props.task.completed) {
            const updatedTask = {
                ...props.task,
                completed: !props.task.completed,
                status: 'success'
            };
            props.toggleCompleted(updatedTask);
            props.soundbites.completed.play();
        }
        else {
            const updatedTask = {
                ...props.task,
                completed: !props.task.completed,
                status: 'danger'
            }
            props.toggleCompleted(updatedTask);
            props.soundbites.incomplete.play();
        }
    }

    return (
        <div className='Task'>
            <Button 
                variant={props.task.status} 
                onClick={clickButton}
                ><p>{task.task}</p></Button>
        </div>
    );
};

export default Task;
