import React from 'react';
import Button from 'react-bootstrap/Button';
import '../../sass/Todo/Task.scss';


const Task = (props) => {
    const incomplete = 'danger';
    const complete = 'success';

    const clickButton = () => {

        const { id } = props.task;

        if (props.task.status === incomplete) {
            const updatedTask = {
                id: id,
                task: props.task.task,
                completed: !props.task.completed
            };
            props.toggleCompleted(id, updatedTask);
            props.soundbites.completed.play();
        }
        else {
            const updatedTask = {
                id: id,
                task: props.task.task,
                completed: !props.task.completed
            }
            props.toggleCompleted(id, updatedTask);
            props.soundbites.incomplete.play();
        }
    }

    return (
        <div className='Task'>
            <Button 
                variant={props.task.status} 
                onClick={clickButton}
                ><p>{props.task.task}</p></Button>
        </div>
    );
};

export default Task;
